    /**
     * Test collect without generating qr-code.
     */
    @Test
    public void collectPendingNoQr() throws SignatureParseException {
        CollectResponse collectResponse = new CollectResponse();
        collectResponse.setOrderRef("orderInfo123");
        collectResponse.setStatus("pending");
        collectResponse.setHintCode("started");
        when(this.rpApi.collect(Mockito.anyString())).thenReturn(collectResponse);

        BankIDTransaction transaction = new BankIDTransaction(
            "orderInfo123",
            "67df3917-fa0d-44e5-b327-edcc928297f8",
            "d28db9a7-4cde-429e-a983-359be676944c",
            "autoStartToken",
            Instant.now()
        );

        CollectResult response = this.bankIDService.collect(transaction);

        assertEquals(Status.PENDING, response.getStatus());
        assertEquals("started", response.getHintCode());
        assertNull(response.getQrCode());
    }

    /**
     * Test collect with status complete.
     */
    @Test
    public void collectComplete() throws SignatureParseException {
        UserData userData = new UserData();
        userData.setName("Test Name");
        userData.setPersonalNumber("123456789012");
        CompletionData completionData = new CompletionData();
        completionData.setUser(userData);
        completionData.setSignature(this.signature);
        completionData.setOcspResponse(this.ocspResponse);

        CollectResponse collectResponse = new CollectResponse();
        collectResponse.setOrderRef("orderInfo123");
        collectResponse.setStatus("complete");
        collectResponse.setCompletionData(completionData);
        when(this.rpApi.collect(Mockito.anyString())).thenReturn(collectResponse);

        BankIDTransaction transaction = new BankIDTransaction(
            "orderInfo123",
            "67df3917-fa0d-44e5-b327-edcc928297f8",
            "d28db9a7-4cde-429e-a983-359be676944c",
            "autoStartToken",
            Instant.now()
        );

        CollectResult response = this.bankIDService.collect(transaction);

        assertEquals(Status.COMPLETE, response.getStatus());
        assertNull(response.getQrCode());
        assertNull(response.getHintCode());
        assertNotNull(response.getCompletionResult());
        assertEquals("Test Name", response.getCompletionResult().getName());
        assertEquals("123456789012", response.getCompletionResult().getPersonalNumber());
        assertEquals("signeringstext", response.getCompletionResult().getSignedText());
    }

